import React from 'react'
import { useState } from 'react'

import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';

function App() {

  let [isEditProfilePopupOpen, setEditProfilePopupOpen] = useState(false);
  let [isAddPlacePopupOpen, setAddPlacePopupOpen] = useState(false);
  let [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = useState(false);



  function handleEditAvatarClick() {
    setEditAvatarPopupOpen(true)
  }

  function handleEditProfileClick() {
    setEditProfilePopupOpen(true)
  }

  function handleAddPlaceClick() {
    setAddPlacePopupOpen(true)
  }

  function profileChild() {
    return (
      <>
      <div className="popup__section">
        <input type="text" required className="popup__input popup__input_type_name" defaultValue name="name" placeholder="Введите ваше имя" minLength={2} maxLength={40} />
        <span className="popup__input-error popup__input-error_position_up" />
      </div>
      <div className="popup__section">
        <input type="text" required className="popup__input popup__input_type_job" defaultValue name="about" placeholder="Введите вашу профессию" minLength={2} maxLength={200} />
        <span className="popup__input-error popup__input-error_position_dawn" />
      </div>
      </>
    )
  }


  return (
    <div>
    <Header />
    <Main onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onEditAvatar={handleEditAvatarClick}/>
    <Footer />
    <template className="template" >
      <div className="card card_margin_bottom">
        <button className="card__delete-button hidden" type="button"></button>
        <img src="image" alt="image" className="card__image" />
        <div className="card__title-and-button">
          <h2 className="card__title"></h2>
          <div className="card__container">
            <button className="card__like-button" type="button"></button>
            <p className="card__like-counter">0</p>
          </div>
        </div>
      </div>
    </template>
    <PopupWithForm name='profile' title='Редактировать профиль' isOpen={isEditProfilePopupOpen} children={profileChild} />
    <PopupWithForm name='card' title='Новое место' isOpen={isAddPlacePopupOpen} children='' />
    <PopupWithForm name='avatar' title='Обновить аватар' isOpen={isEditAvatarPopupOpen} children='' />
  </div>
  )
}

export default App
