import React from 'react'
import { useState } from 'react'

import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';

function App() {

  let [isEditProfilePopupOpen, setEditProfilePopupOpen] = useState(false);  //Стэйт профиля
  let [isAddPlacePopupOpen, setAddPlacePopupOpen] = useState(false);  //Стэйт карточки
  let [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = useState(false);  //Стэйт аватара







  let [selectedCard, setSelectedCard] = useState(false);  //


  function handleCardClick() {
    setSelectedCard(true)
  }








  //Обработчик открытия попапа изменения аватара
  function handleEditAvatarClick() {
    setEditAvatarPopupOpen(true)
  }

  //Обработчик открытия попапа редактирования профиля
  function handleEditProfileClick() {
    setEditProfilePopupOpen(true)
  }

  //Обработчик открытия попапа добавления карточки
  function handleAddPlaceClick() {
    setAddPlacePopupOpen(true)
  }

  //Обработчик закрытия всех попапов
  function closeAllPopups() {
    setAddPlacePopupOpen(false)
    setEditAvatarPopupOpen(false)
    setEditProfilePopupOpen(false)
    setSelectedCard(false)
  }

  return (
    <div>
    <Header />
    <Main onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onEditAvatar={handleEditAvatarClick}/>
    <Footer />
    <template className="template" >
      <div className="card card_margin_bottom">
        <button className="card__delete-button hidden" type="button"></button>
        <img src="image" alt="card" className="card__image" />
        <div className="card__title-and-button">
          <h2 className="card__title">-</h2>
          <div className="card__container">
            <button className="card__like-button" type="button"></button>
            <p className="card__like-counter">0</p>
          </div>
        </div>
      </div>
    </template>
    <PopupWithForm name='profile' title='Редактировать профиль' isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} children='' />
    <PopupWithForm name='card' title='Новое место' isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} children='' />
    <PopupWithForm name='avatar' title='Обновить аватар' isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} children='' />
  </div>
  )
}

export default App
