import React from "react"

function ImagePopup(props) {

  //Переменная для присвоения класса открытого попапа
  let open = null

  //Проверка стэйта
  if (props.card) {
    open = 'popup_opened'
    console.log(props.card)
  }

  return (
    <div className={`image-popup popup popup_type_image ${open}`}>
      <div className="image-popup__container">
        <button className="popup__close-button popup__close-button_type_image" type="button" aria-label="Закрыть" />
        <img src={props.card} alt="this-pic" className="image-popup__image" />
        <h2 className="image-popup__title" />
      </div>
    </div>
  )
}

export default ImagePopup
