import React from "react"

function ImagePopup(props) {


  if (props.card) {
        console.log(props.card)
      }

  return (
    <div className={`image-popup popup popup_type_image ${props.card.link ? 'popup__opened' : ''}`}>
      <div className="image-popup__container">
        <button className="popup__close-button popup__close-button_type_image" type="button" aria-label="Закрыть" onClick={props.onClose}/>
        <img src={props.card} alt="this-pic" className="image-popup__image" />
        <h2 className="image-popup__title" />
      </div>
    </div>
  )
}

export default ImagePopup
