import React from 'react'
import { useState } from 'react'

import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';

function App() {

  let [isEditProfilePopupOpen, setEditProfilePopupOpen] = useState(false);
  let [isAddPlacePopupOpen, setAddPlacePopupOpen] = useState(false);
  let [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = useState(false);



  function handleEditAvatarClick() {
    setEditAvatarPopupOpen(true)
  }

  function handleEditProfileClick() {
    setEditProfilePopupOpen(true)
  }

  function handleAddPlaceClick() {
    setAddPlacePopupOpen(true)
  }

  function closeAllPopups() {
    setAddPlacePopupOpen(false)
    setEditAvatarPopupOpen(false)
    setEditProfilePopupOpen(false)
  }


  function closeAllPopupsOver(e) {
    if (e.target.classList.contains('popup')) {
      setAddPlacePopupOpen(false)
      setEditAvatarPopupOpen(false)
      setEditProfilePopupOpen(false)
    }
  }


  return (
    <div>
    <Header />
    <Main onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onEditAvatar={handleEditAvatarClick}/>
    <Footer />
    <template className="template" >
      <div className="card card_margin_bottom">
        <button className="card__delete-button hidden" type="button"></button>
        <img src="image" alt="image" className="card__image" />
        <div className="card__title-and-button">
          <h2 className="card__title"></h2>
          <div className="card__container">
            <button className="card__like-button" type="button"></button>
            <p className="card__like-counter">0</p>
          </div>
        </div>
      </div>
    </template>
    <PopupWithForm name='profile' title='Редактировать профиль' isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} children='' onCloseOver={closeAllPopupsOver}/>
    <PopupWithForm name='card' title='Новое место' isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} children='' onCloseOver={closeAllPopupsOver}/>
    <PopupWithForm name='avatar' title='Обновить аватар' isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} children='' onCloseOver={closeAllPopupsOver}/>
  </div>
  )
}

export default App
